import React, { useState, useEffect } from 'react';
import expensedetails from './expensedetails';

const Expense = () => {
  const [expenses, setExpenses] = useState([]);
  const [editingExpenseId, setEditingExpenseId] = useState(null);

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = async () => {
    try {
      const res = await expensedetails.getExpenses();
      setExpenses(res.data);
    } catch (error) {
      console.error('Failed to fetch expenses:', error);
    }
  };

  const handleEditClick = (id) => {
    setEditingExpenseId(id);
  };

  const handleCancelEdit = () => {
    setEditingExpenseId(null);
  };

  const handleInputChange = (e, id, field) => {
    const updatedExpenses = expenses.map((exp) =>
      exp.id === id ? { ...exp, [field]: e.target.value } : exp
    );
    setExpenses(updatedExpenses);
  };

  const handleSave = async (id) => {
    try {
      await expensedetails.updateExpense(id, {
        amount: expenses.find((exp) => exp.id === id).amount,
        category: expenses.find((exp) => exp.id === id).category,
        date: expenses.find((exp) => exp.id === id).date,
      });
      setEditingExpenseId(null);
      fetchExpenses(); // Refresh the list
    } catch (error) {
      console.error('Failed to update expense:', error);
    }
  };

  return (
    <div>
      <h2>Expense Tracker</h2>
      <ul>
        {expenses.map((exp, index) => (
          <li key={exp.id} style={{ cursor: 'pointer' }}>
            {editingExpenseId === exp.id ? (
              <div>
                <input
                  type="number"
                  value={exp.amount}
                  onChange={(e) => handleInputChange(e, exp.id, 'amount')}
                />
                <input
                  type="text"
                  value={exp.category}
                  onChange={(e) => handleInputChange(e, exp.id, 'category')}
                />
                <input
                  type="date"
                  value={exp.date}
                  onChange={(e) => handleInputChange(e, exp.id, 'date')}
                />
                <button onClick={() => handleSave(exp.id)}>Save</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </div>
            ) : (
              <div onClick={() => handleEditClick(exp.id)}>
                {index + 1}. â‚¹{exp.amount} - {exp.category} - {exp.date}
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Expense;
