import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import expensedetails from "./expensedetails";

const Expense = () => {
  const navigate = useNavigate();
  const [amount, setamount] = useState('');
  const [category, setcategory] = useState('');
  const [date, setdate] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [selectedExpense, setSelectedExpense] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const handleNavigate = () => {
    navigate("/add");
  };

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = async () => {
    try {
      const res = await expensedetails.getExpenses();
      setExpenses(res.data);
    } catch (error) {
      console.error("Failed to fetch expenses:", error);
    }
  };

  const handleDelete = async (id) => {
    const isConfirmed = window.confirm("Are you sure you want to delete this expense?");
  
    if (isConfirmed) {
      try {
        await expensedetails.deleteExpense(id);
        setSelectedExpense(null); 
        fetchExpenses();
      } catch (error) {
        console.error("Delete failed:", error);
      }
    }
  };
  

  return (
    <div>
      <h2>Expense Tracker</h2>
      <button onClick={handleNavigate}>Add</button>

      <h3>MY Expenses</h3>
      <p><strong>Total Amount: ₹{expenses.reduce((total, exp) => total + parseFloat(exp.amount), 0)}</strong></p>
      <ul>
        {expenses.map((exp, index) => (
          <li
            key={exp.id}
            onClick={() => setSelectedExpense(exp)}
            style={{ cursor: "pointer" }}
          >
            {index + 1}. ₹{exp.amount} - {exp.category} - {exp.date}
          </li>
        ))}
      </ul>

      {selectedExpense && (
        <div style={{ border: "1px solid gray", padding: "10px", marginTop: "20px" }}>
          <h4>Expense Details</h4>
          <p><strong>Amount:</strong> ₹{selectedExpense.amount}</p>
          <p><strong>Category:</strong> {selectedExpense.category}</p>
          <p><strong>Date:</strong> {selectedExpense.date}</p>
          <button onClick={() => {
        setShowForm(!showForm);

          }}>
          {showForm ?   'Edit' : 'Cancel'}
          </button>

          {showForm && (
        <div >
          <input
            
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <input
            
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />
          <input
            
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
          <button onClick={handleAddTask}>{editingTask ? 'Update Task' : 'Add Task'}</button>
        </div>
      )}

          


          <button onClick={() => handleDelete(selectedExpense.id)}>Delete</button>
        </div>
      )}
    </div>
  );
};

export default Expense;
