import React, { useState, useEffect } from "react";
import expensedetails from "./expensedetails";

const Expense = () => {
  const [expenses, setExpenses] = useState([]);
  const [selectedExpense, setSelectedExpense] = useState(null);
  const [editedExpense, setEditedExpense] = useState({ amount: "", category: "", date: "" });

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = async () => {
    try {
      const res = await expensedetails.getExpenses();
      setExpenses(res.data);
    } catch (error) {
      console.error("Failed to fetch expenses:", error);
    }
  };

  const handleEditClick = (expense) => {
    setSelectedExpense(expense);
    setEditedExpense({
      amount: expense.amount,
      category: expense.category,
      date: expense.date,
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedExpense((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    try {
      await expensedetails.updateExpense(selectedExpense.id, editedExpense);
      fetchExpenses();
      setSelectedExpense(null);
    } catch (error) {
      console.error("Failed to update expense:", error);
    }
  };

  const handleDelete = async (id) => {
    const isConfirmed = window.confirm("Are you sure you want to delete this expense?");
    if (isConfirmed) {
      try {
        await expensedetails.deleteExpense(id);
        fetchExpenses();
      } catch (error) {
        console.error("Delete failed:", error);
      }
    }
  };

  return (
    <div>
      <h2>Expense Tracker</h2>
      <h3>MY Expenses</h3>
      <p>
        <strong>
          Total Amount: ₹
          {expenses.reduce((total, exp) => total + parseFloat(exp.amount), 0)}
        </strong>
      </p>
      <ul>
        {expenses.map((exp, index) => (
          <li
            key={exp.id}
            onClick={() => handleEditClick(exp)}
            style={{ cursor: "pointer" }}
          >
            {index + 1}. ₹{exp.amount} - {exp.category} - {exp.date}
          </li>
        ))}
      </ul>

      {selectedExpense && (
        <div style={{ border: "1px solid gray", padding: "10px", marginTop: "20px" }}>
          <h4>Edit Expense</h4>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              handleSave();
            }}
          >
            <input
              type="text"
              name="amount"
              value={editedExpense.amount}
              onChange={handleChange}
              placeholder="Amount"
            />
            <input
              type="text"
              name="category"
              value={editedExpense.category}
              onChange={handleChange}
              placeholder="Category"
            />
            <input
              type="date"
              name="date"
              value={editedExpense.date}
              onChange={handleChange}
            />
            <button type="submit">Save</button>
            <button type="button" onClick={() => setSelectedExpense(null)}>
              Cancel
            </button>
          </form>
          <button onClick={() => handleDelete(selectedExpense.id)}>Delete</button>
        </div>
      )}
    </div>
  );
};

export default Expense;
